"use strict";
const electron = require("electron");
electron.contextBridge.exposeInMainWorld("electronAPI", {
  // 文件系统操作
  getFiles: (dirPath) => electron.ipcRenderer.invoke("get-files", dirPath),
  getFileContent: (filePath) => electron.ipcRenderer.invoke("get-file-content", filePath),
  // 打开文件夹对话框
  onOpenFolder: (callback) => {
    electron.ipcRenderer.removeAllListeners("open-folder");
    return electron.ipcRenderer.on("open-folder", () => callback());
  },
  openFolderDialog: () => electron.ipcRenderer.send("open-folder-dialog"),
  onSelectedFolder: (callback) => {
    electron.ipcRenderer.removeAllListeners("selected-folder");
    return electron.ipcRenderer.on("selected-folder", (_event, folderPath) => callback(folderPath));
  }
  // 可以在这里添加更多的API
});
